import { Box, Typography } from '@mui/material'
import React from 'react'

function Mark({title, type, text, children, bold, italicized, underlined, variableType, color, clauseCounter}) {

  console.log(type, text, children, clauseCounter)

  const getChildren = () => children.map((child, index) => {
    const isBold = child.bold || bold
    const isItalicized = child.italicized || italicized
    const isUnderlined = child.underlined || underlined
    const isList = type === "ul" || type === "ol"
    const childJsx = <Mark
      key={!isList && index}
      title={child.title}
      type={child.type}
      text={child.text}
      bold={child.bold}
      italicized={isBold}
      underlined={isItalicized}
      variableType={isUnderlined}
      color={child.color}
      clauseCounter={clauseCounter}
      children={child.children}
    />
    if (isList) return <li key={index}>{childJsx}</li>
    else return childJsx
  })

  const getTypeWrapper = () => {
    switch(type) {
      case "block":
        return (children) => <Box component="div" sx={{ display: 'block' }}>{children}</Box>
      case "p":
        // we do span here because we can't nest lists, paragraphs, etc. in a paragraph
        return (children) => <span>{children}</span>
      case "h1":
        return (children) => <h1>{children}</h1>
      case "h2":
        return (children) => <h2>{children}</h2>
      case "h3":
        return (children) => <h3>{children}</h3>
      case "h4":
        return (children) => <h4>{children}</h4>
      case "h5":
        return (children) => <h5>{children}</h5>
      case "h6":
        return (children) => <h6>{children}</h6>
      case "ul":
        return (children) => <ul>{children}</ul>
      case "ol":
        return (children) => <ol>{children}</ol>
      case "span":
        return (children) => <span>{children}</span>
      case "clause":
        const current = clauseCounter.current++
        return (children) => <span>{current}. {children}</span>
      default:
        return (children) => <span>{children}</span>
    }
  }

  if(text !== undefined) return <Typography component="span" bgcolor={color} sx={{fontWeight: bold ? 'bold' : 'normal', fontStyle: italicized ? 'italic' : 'normal', textDecoration: underlined ? 'underline' : 'none'}}>{text}</Typography>
  else return (
    getTypeWrapper()(getChildren())
  )
}


export default Mark
